import React, { useState, useEffect } from "react";
import { fetchAllCVs, updateCV, deleteCV } from "../services/api";

const ManageCVs = () => {
  const [cvList, setCvList] = useState([]);
  const [selectedCV, setSelectedCV] = useState(null);
  const [cv, setCv] = useState({
    personalInfo: { name: "", email: "", phone: "" },
    skills: "",
    education: "",
    experience: "",
    references: "",
  });

  useEffect(() => {
    const fetchCVs = async () => {
      try {
        const cvs = await fetchAllCVs();
        setCvList(cvs);
      } catch (error) {
        alert("Feil ved henting av CV-er.");
      }
    };

    fetchCVs();
  }, []);

  const handleEdit = (cvItem) => {
    setCv(cvItem);
    setSelectedCV(cvItem);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCv({ ...cv, personalInfo: { ...cv.personalInfo, [name]: value } });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const updatedCV = { ...cv, userId: selectedCV.userId || "admin" };
      await updateCV(selectedCV._id, updatedCV);
      alert("CV oppdatert!");

      setCvList((prev) =>
        prev.map((item) => (item._id === selectedCV._id ? updatedCV : item))
      );
      setCv({
        personalInfo: { name: "", email: "", phone: "" },
        skills: "",
        education: "",
        experience: "",
        references: "",
      });
      setSelectedCV(null);
    } catch (error) {
      console.error("Feil ved oppdatering av CV:", error);
      alert("Kunne ikke oppdatere CV.");
    }
  };

  const handleDelete = async (cvId) => {
    try {
      await deleteCV(cvId);
      alert("CV slettet!");
      setCvList(cvList.filter((item) => item._id !== cvId));
    } catch (error) {
      alert("Feil ved sletting av CV.");
    }
  };

  return (
    <div className="p-8 min-h-screen bg-gradient-to-r from-[#90E0EF] to-[#CAF0F8]">
      <h1 className="mb-8 text-3xl font-bold text-[#023E8A] text-center">
        Administrer CV-er
      </h1>
      <form
        onSubmit={handleSubmit}
        className="max-w-md p-8 mx-auto bg-white rounded-lg shadow-lg"
      >
        <h2 className="mb-4 text-xl text-[#023E8A] text-center">Rediger CV</h2>

        <label className="block mb-2">Navn:</label>
        <input
          type="text"
          name="name"
          value={cv.personalInfo.name}
          onChange={handleChange}
          required
          className="w-full p-3 mb-4 border-2 rounded-lg focus:outline-none focus:ring-2"
        />

        <label className="block mb-2">E-post:</label>
        <input
          type="email"
          name="email"
          value={cv.personalInfo.email}
          onChange={handleChange}
          required
          className="w-full p-3 mb-4 border-2 rounded-lg focus:outline-none focus:ring-2"
        />

        <label className="block mb-2">Telefonnummer:</label>
        <input
          type="tel"
          name="phone"
          value={cv.personalInfo.phone}
          onChange={handleChange}
          required
          className="w-full p-3 mb-4 border-2 rounded-lg focus:outline-none focus:ring-2"
        />

        <button
          type="submit"
          className="w-full p-3 mt-4 text-white bg-blue-600 rounded-lg hover:bg-blue-800"
        >
          Oppdater CV
        </button>
      </form>

      <h2 className="mt-8 mb-4 text-xl text-[#023E8A]">Alle CV-er</h2>
      <ul className="space-y-4">
        {cvList.map((cvItem) => (
          <li
            key={cvItem._id}
            className="p-4 bg-white border rounded-lg shadow-lg"
          >
            <p>
              <strong>Navn:</strong> {cvItem.personalInfo.name}
            </p>
            <p>
              <strong>E-post:</strong> {cvItem.personalInfo.email}
            </p>
            <p>
              <strong>Telefonnummer:</strong> {cvItem.personalInfo.phone}
            </p>
            <button
              onClick={() => handleEdit(cvItem)}
              className="w-full p-2 mt-2 text-white bg-blue-600 rounded-lg hover:bg-blue-800"
            >
              Rediger
            </button>
            <button
              onClick={() => handleDelete(cvItem._id)}
              className="w-full p-2 mt-2 text-white bg-red-600 rounded-lg hover:bg-red-800"
            >
              Slett
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageCVs;
