      localStorage.setItem("token", token); // Lagre tokenet
      navigate("/users"); // Naviger til adminbrukerens side, for eksempel
    } catch (error) {
      console.error("Login failed:", error);
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <form
        className="p-8 bg-white rounded shadow-md w-96"
        onSubmit={handleSubmit}
      >
        <h2 className="text-2xl font-bold mb-6 text-center">Logg inn</h2>

        <label className="block mb-2 text-gray-700">E-post</label>
        <input
          type="email"
          value={email}
          placeholder="Eks: olaandersen@gmail.com"
          onChange={(e) => setEmail(e.target.value)}
          className="w-full p-2 mb-4 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        />

        <label className="block mb-2 text-gray-700">Passord</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-full p-2 mb-6 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        />

        <button
          type="submit"
          className="w-full p-2 text-white bg-blue-500 rounded hover:bg-blue-600"
        >
          Logg inn
        </button>
      </form>
    </div>
  );
}

export default Login;
