import React from "react";
import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";

function LoginPage() {
  const { register, handleSubmit } = useForm();
  const navigate = useNavigate();

  const onSubmit = (data) => {
    const { username, password } = data;

    // Hent alle brukere fra CRUD CRUD API
    fetch(`${API_BASE}/users`)
      .then((response) => response.json())
      .then((users) => {
        // Finn en bruker som matcher både brukernavn og passord
        const user = users.find(
          (user) => user.username === username && user.password === password
        );

        if (user) {
          // Lagre brukerens rolle og brukernavn i localStorage
          localStorage.setItem("role", user.role);
          localStorage.setItem("username", user.username); // Lagre brukernavnet hvis nødvendig
          navigate("/dashboard"); // Naviger til dashboardet
        } else {
          alert("Feil brukernavn eller passord.");
        }
      })
      .catch((error) => {
        console.error("Feil ved henting av brukere:", error);
        alert(
          "Det oppstod en feil ved serverkommunikasjon. Vennligst prøv igjen senere."
        );
      });
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-100">
      <form
        className="p-8 bg-white rounded shadow-md w-96"
        onSubmit={handleSubmit(onSubmit)}
      >
        <h2 className="mb-6 text-2xl font-bold text-center">
          Logg inn for å registrere CV
        </h2>

        <label className="block mb-2 text-gray-700">Brukernavn:</label>
        <input
          type="text"
          {...register("username", { required: true })}
          placeholder="Eks: olaandersen"
          className="w-full p-2 mb-4 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
        />

        <label className="block mb-2 text-gray-700">Passord:</label>
        <input
          type="password"
          {...register("password", { required: true })}
          className="w-full p-2 mb-6 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
        />

        <button
          type="submit"
          className="w-full p-2 text-white bg-blue-500 rounded hover:bg-blue-600"
        >
          Logg inn
        </button>
      </form>
    </div>
  );
}

export default LoginPage;
