import React from "react";
import CVForm from "../components/CVForm";
import { createCV } from "../services/api";
import Navbar from "../components/Navbar";
import html2pdf from "html2pdf.js";

function UserCV() {
  const handleCVSubmit = (data) => {
    // Sender data til API'et for å opprette en CV
    createCV(data)
      .then((response) => {
        console.log("CV opprettet:", response);
      })
      .catch((error) => {
        console.error("Feil ved oppretting av CV:", error);
        alert("Kunne ikke opprette CV");
      });
  };

  const exportToPDF = () => {
    const element = document.getElementById("cv-content"); // Hent CV-elementet
    const options = {
      filename: "CV.pdf", // Navn på PDF
      image: { type: "jpeg", quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: "mm", format: "a4", orientation: "portrait" },
    };

    html2pdf().from(element).set(options).save();
  };

  return (
    <div className="p-6">
      <Navbar />
      <CVForm onSubmit={handleCVSubmit} />

      {/* Eksporter til PDF-knapp */}
      <button
        onClick={exportToPDF}
        className="p-3 mt-4 text-white bg-blue-500 rounded-lg"
      >
        Eksporter til PDF
      </button>
    </div>
  );
}

export default UserCV;
