import React from "react";
import CVForm from "../components/CVForm";
import { createCV } from "../services/api"; // Importer API-funksjonen for å lage CV
import Navbar from "../components/Navbar";
import { useNavigate } from "react-router-dom";

function UserCV() {
  const navigate = useNavigate(); // Hook for navigasjon

  const handleCVSubmit = (data) => {
    // Hent brukerens ID fra localStorage
    const currentUserId = localStorage.getItem("userId");
    console.log("Current User ID:", currentUserId);

    // Hvis ingen bruker er logget inn, naviger til login-siden
    if (!currentUserId) {
      alert("Brukeren er ikke logget inn.");
      navigate("/login"); // Naviger til login-siden
      return;
    }

    // Legg til brukerens ID i dataene før du sender dem til API-et
    const newCv = {
      ...data,
      userId: currentUserId, // Legg til brukerens ID sammen med CV-dataene
    };

    // Her sender vi dataene til API-et for å opprette en CV
    createCV(newCv)
      .then((response) => {
        console.log("CV opprettet:", response);
        // Naviger til et annet sted, som dashboard eller vis en bekreftelse
      })
      .catch((error) => {
        console.error("Feil ved oppretting av CV:", error);
        alert("Kunne ikke opprette CV");
      });
  };

  return (
    <div className="p-6">
      <Navbar />
      <h1 className="mb-4 text-3xl font-semibold">Min CV</h1>
      <CVForm onSubmit={handleCVSubmit} />
    </div>
  );
}

export default UserCV;
