import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  createCV,
  updateCV,
  deleteCV,
  fetchCVsByUserId,
} from "../services/api";

const CVForm = () => {
  const [cv, setCv] = useState({
    personalInfo: { name: "", email: "", phone: "" },
    skills: [],
    education: [],
    experience: [],
    references: [],
  });
  const [cvList, setCvList] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [id, setId] = useState(null); // For edit purposes

  useEffect(() => {
    const currentUserId = localStorage.getItem("userId");

    if (!currentUserId) {
      alert("Brukeren er ikke logget inn.");
      navigate("/login");
      return;
    }

    const fetchUserCVs = async () => {
      setLoading(true);
      try {
        const cvs = await fetchCVsByUserId(currentUserId);
        const filteredCVs = cvs.filter((cv) => cv.userId === currentUserId);
        setCvList(filteredCVs);
      } catch (error) {
        alert("Feil ved henting av CV-er.");
      } finally {
        setLoading(false);
      }
    };

    fetchUserCVs();
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const currentUserId = localStorage.getItem("userId");

    if (!currentUserId) {
      alert("Brukeren er ikke logget inn.");
      navigate("/login");
      return;
    }

    try {
      const newCv = {
        ...cv,
        userId: currentUserId,
      };

      if (isEditing) {
        await updateCV(id, newCv);
        alert("CV oppdatert!");
      } else {
        await createCV(newCv);
        alert("CV opprettet!");
        setCvList([...cvList, newCv]);
      }

      setCv({
        personalInfo: { name: "", email: "", phone: "" },
        skills: [],
        education: [],
        experience: [],
        references: [],
      });
      setIsEditing(false);
    } catch (error) {
      console.error("Feil ved oppretting eller oppdatering av CV:", error);
      alert("Feil ved oppretting eller oppdatering av CV.");
    }
  };

  const handleDelete = async (cvId) => {
    try {
      await deleteCV(cvId);
      alert("CV slettet!");
      setCvList(cvList.filter((item) => item._id !== cvId));
      navigate("/cvs");
    } catch (error) {
      alert("Feil ved sletting av CV.");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCv({ ...cv, personalInfo: { ...cv.personalInfo, [name]: value } });
  };

  const handleArrayChange = (e, type) => {
    const { value } = e.target;
    setCv((prevState) => {
      const updatedArray = prevState[type].includes(value)
        ? prevState[type]
        : [...prevState[type], value];
      return { ...prevState, [type]: updatedArray };
    });
  };

  const handleRemoveArrayItem = (index, type) => {
    setCv((prevState) => {
      const updatedArray = prevState[type].filter((_, i) => i !== index);
      return { ...prevState, [type]: updatedArray };
    });
  };

  const handleEdit = (cvItem) => {
    setCv({
      personalInfo: {
        name: cvItem.personalInfo.name,
        email: cvItem.personalInfo.email,
        phone: cvItem.personalInfo.phone,
      },
      skills: cvItem.skills,
      education: cvItem.education,
      experience: cvItem.experience,
      references: cvItem.references,
    });
    setIsEditing(true);
    setId(cvItem._id);
  };

  return (
    <div className="p-8 bg-gradient-to-r from-[#90E0EF] to-[#CAF0F8] min-h-screen">
      <h1 className="mb-20 text-3xl font-bold text-[#023E8A] text-center">
        {isEditing ? "Rediger CV" : "Opprett CV"}
      </h1>

      <form
        onSubmit={handleSubmit}
        className="max-w-md p-10 mx-auto bg-white rounded-lg shadow-lg"
      >
        <h2 className="mb-4 text-xl text-[#023E8A] text-center">
          {isEditing ? "Oppdater CV" : "Opprett ny CV"}
        </h2>

        <label className="block mb-2">Navn:</label>
        <input
          type="text"
          name="name"
          value={cv.personalInfo.name}
          onChange={handleChange}
          required
          className="w-full p-3 mb-4 border-2 border-[#023E8A] rounded-lg focus:outline-none focus:ring-2 focus:ring-[#0077B6]"
        />

        <label className="block mb-2">E-post:</label>
        <input
          type="email"
          name="email"
          value={cv.personalInfo.email}
          onChange={handleChange}
          required
          className="w-full p-3 mb-4 border-2 border-[#023E8A] rounded-lg focus:outline-none focus:ring-2 focus:ring-[#0077B6]"
        />

        <label className="block mb-2">Telefonnummer:</label>
        <input
          type="tel"
          name="phone"
          value={cv.personalInfo.phone}
          onChange={handleChange}
          required
          className="w-full p-3 mb-4 border-2 border-[#023E8A] rounded-lg focus:outline-none focus:ring-2 focus:ring-[#0077B6]"
        />

        <label className="block mb-2">Ferdigheter:</label>
        <input
          type="text"
          placeholder="Legg til ferdighet"
          onBlur={(e) => handleArrayChange(e, "skills")}
          className="w-full p-3 mb-4 border-2 border-[#023E8A] rounded-lg focus:outline-none focus:ring-2 focus:ring-[#0077B6]"
        />
        <ul className="mb-4">
          {cv.skills.map((skill, index) => (
            <li key={index} className="flex justify-between">
              {skill}{" "}
              <button
                type="button"
                onClick={() => handleRemoveArrayItem(index, "skills")}
                className="text-red-500"
              >
                X
              </button>
            </li>
          ))}
        </ul>

        <label className="block mb-2">Utdanning:</label>
        <input
          type="text"
          placeholder="Institusjon"
          onBlur={(e) => handleArrayChange(e, "education")}
          className="w-full p-3 mb-4 border-2 border-[#023E8A] rounded-lg focus:outline-none focus:ring-2 focus:ring-[#0077B6]"
        />
        <ul className="mb-4">
          {cv.education.map((edu, index) => (
            <li key={index} className="flex justify-between">
              {edu}{" "}
              <button
                type="button"
                onClick={() => handleRemoveArrayItem(index, "education")}
                className="text-red-500"
              >
                X
              </button>
            </li>
          ))}
        </ul>

        <label className="block mb-2">Erfaring:</label>
        <input
          type="text"
          placeholder="Tittel"
          onBlur={(e) => handleArrayChange(e, "experience")}
          className="w-full p-3 mb-4 border-2 border-[#023E8A] rounded-lg focus:outline-none focus:ring-2 focus:ring-[#0077B6]"
        />
        <ul className="mb-4">
          {cv.experience.map((exp, index) => (
            <li key={index} className="flex justify-between">
              {exp}{" "}
              <button
                type="button"
                onClick={() => handleRemoveArrayItem(index, "experience")}
                className="text-red-500"
              >
                X
              </button>
            </li>
          ))}
        </ul>

        <label className="block mb-2">Referanser:</label>
        <input
          type="text"
          placeholder="Navn på referanse"
          onBlur={(e) => handleArrayChange(e, "references")}
          className="w-full p-3 mb-4 border-2 border-[#023E8A] rounded-lg focus:outline-none focus:ring-2 focus:ring-[#0077B6]"
        />
        <ul className="mb-4">
          {cv.references.map((ref, index) => (
            <li key={index} className="flex justify-between">
              {ref}{" "}
              <button
                type="button"
                onClick={() => handleRemoveArrayItem(index, "references")}
                className="text-red-500"
              >
                X
              </button>
            </li>
          ))}
        </ul>

        <button
          type="submit"
          className="mt-8 w-full p-3 text-white bg-[#0077B6] rounded-lg hover:bg-[#023E8A] focus:ring-4 focus:ring-[#90E0EF]"
        >
          {isEditing ? "Oppdater CV" : "Opprett CV"}
        </button>

        {isEditing && (
          <button
            type="button"
            onClick={() => handleDelete(id)}
            className="mt-4 w-full p-3 text-white bg-red-500 rounded-lg hover:bg-red-600 focus:ring-4 focus:ring-[#90E0EF]"
          >
            Slett CV
          </button>
        )}
      </form>

      <h2 className="mt-16 mb-4 text-xl text-[#023E8A]">Din CV:</h2>
      <ul className="space-y-4">
        {cvList.map((cvItem, index) => (
          <li
            key={cvItem._id || index}
            className="p-4 bg-white border rounded-lg shadow-lg"
          >
            <p>
              <strong>Navn:</strong> {cvItem.personalInfo.name}
            </p>
            <p>
              <strong>E-post:</strong> {cvItem.personalInfo.email}
            </p>
            <p>
              <strong>Telefonnummer:</strong> {cvItem.personalInfo.phone}
            </p>
            <button
              onClick={() => handleEdit(cvItem)}
              className="mt-2 w-full p-2 text-white bg-[#023E8A] rounded-lg hover:bg-[#0077B6]"
            >
              Rediger
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CVForm;
