import React, { useState, useEffect } from "react";

const API_BASE = "https://crudcrud.com/api/07b637f165124acb8ccc0cff42612b46";

function ManageUsers() {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({
    name: "",
    username: "",
    email: "",
    password: "",
    role: "user",
  });

  // Hent alle brukere  
  useEffect(() => {
    fetch(`${API_BASE}/users`)
      .then((response) => response.json())
      .then((data) => setUsers(data))
      .catch((error) => console.error("Feil ved henting av brukere:", error));
  }, []);

  // Registrer ny bruker
  const addUser = (e) => {
    e.preventDefault();
    fetch(`${API_BASE}/users`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newUser),
    })
      .then((response) => response.json())
      .then((data) => {
        setUsers([...users, data]);
        setNewUser({
          name: "",
          username: "",
          email: "",
          password: "",
          role: "user",
        });
      })
      .catch((error) => console.error("Feil ved oppretting av bruker:", error));
  };

  // Slett bruker
  const deleteUser = (id) => {
    fetch(`${API_BASE}/users/${id}`, {
      method: "DELETE",
    })
      .then(() => setUsers(users.filter((user) => user._id !== id)))
      .catch((error) => console.error("Feil ved sletting av bruker:", error));
  };

  return (
    <div className="p-8">
      <h1 className="mb-4 text-2xl font-bold">Brukeradministrasjon</h1>

      <form onSubmit={addUser} className="mb-8">
        <h2 className="mb-4 text-xl">Registrer ny bruker</h2>

        <input
          type="text"
          placeholder="Navn"
          value={newUser.name}
          onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
          className="w-full p-2 mb-4 border rounded"
          required
        />

        <input
          type="text"
          placeholder="Brukernavn"  
          value={newUser.username}
          onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
          className="w-full p-2 mb-4 border rounded"
          required
        />

        <input
          type="email"
          placeholder="E-post"
          value={newUser.email}
          onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
          className="w-full p-2 mb-4 border rounded"
          required
        />

        <input
          type="password"
          placeholder="Passord"
          value={newUser.password}
          onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
          className="w-full p-2 mb-4 border rounded"
          required
        />

        <select
          value={newUser.role}
          onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
          className="w-full p-2 mb-4 border rounded"
        >
          <option value="user">User</option>
          <option value="admin">Admin</option>
        </select>

        <button 
          type="submit"
          className="p-2 text-white bg-blue-500 rounded hover:bg-blue-600"
        >
          Registrer bruker
        </button>
      </form>

      <h2 className="mb-4 text-xl">Eksisterende brukere</h2>
      <ul className="space-y-4">
        {users.map((user) => (
          <li key={user._id} className="p-4 bg-white rounded shadow">
            <p>
              <strong>Navn:</strong> {user.name}
            </p>

            <p>
              <strong>Brukernavn:</strong> {user.username}
            </p>

            <p>
              <strong>E-post:</strong> {user.email}
            </p>

            <p>
              <strong>Passord:</strong> {user.password}
            </p>

            <p>
              <strong>Rolle:</strong> {user.role}
            </p>
            <button
              onClick={() => deleteUser(user._id)}
              className="p-2 mt-2 text-white bg-red-500 rounded hover:bg-red-600"
            >
              Slett
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ManageUsers;
