import React from "react";
import {
  Document,
  Page,
  Text,
  View,
  StyleSheet,
  PDFDownloadLink,
} from "@react-pdf/renderer";

const styles = StyleSheet.create({
  page: {
    backgroundColor: "#ffffff",
    padding: 30,
  },
  section: {
    marginBottom: 15,
  },
  heading: {
    fontSize: 18,
    fontWeight: "bold",
  },
  text: {
    fontSize: 12,
  },
});

const PDFDocument = ({ cvData }) => (
  <Document>
    <Page size="A4" style={styles.page}>
      <View style={styles.section}>
        <Text style={styles.heading}>Navn: {cvData.personalInfo.name}</Text>
        <Text style={styles.text}>E-post: {cvData.personalInfo.email}</Text>
        <Text style={styles.text}>Telefon: {cvData.personalInfo.phone}</Text>
      </View>

      <View style={styles.section}>
        <Text style={styles.heading}>Ferdigheter</Text>
        <Text style={styles.text}>{cvData.skills}</Text>
      </View>

      <View style={styles.section}>
        <Text style={styles.heading}>Utdanning</Text>
        <Text style={styles.text}>{cvData.education}</Text>
      </View>

      <View style={styles.section}>
        <Text style={styles.heading}>Erfaring</Text>
        <Text style={styles.text}>{cvData.experience}</Text>
      </View>

      <View style={styles.section}>
        <Text style={styles.heading}>Referanser</Text>
        <Text style={styles.text}>{cvData.references}</Text>
      </View>
    </Page>
  </Document>
);

export default function PDF({ cvData }) {
  return (
    <div>
      <PDFDownloadLink
        document={<PDFDocument cvData={cvData} />}
        fileName="CV.pdf"
      ></PDFDownloadLink>
    </div>
  );
}
