import React, { useState, useEffect } from "react";
import { createCV, updateCV, deleteCV } from "../services/api"; // Importer API-funksjonene
import Navbar from "../components/Navbar";
import InputField from "../components/InputField";

function UserCV() {
  const [cvs, setCvs] = useState([]);
  const [newCV, setNewCV] = useState({
    personalInfo: { name: "", email: "", phone: "" },
    skills: [],
    education: [],
    experience: [],
    references: [],
  });
  const [searchTerm, setSearchTerm] = useState(""); // For søkefunksjon
  const [editingCV, setEditingCV] = useState(null); // For redigering av CV

  // Hent brukerens egen CV
  useEffect(() => {
    fetchAllCVs()
      .then((data) => {
        const userCVs = data.filter(
          (cv) => cv.userId === "innlogget_bruker_id"
        ); // Filtrer med bruker-ID
        setCvs(userCVs);
      })
      .catch((error) => console.error("Feil ved henting av CV-er:", error));
  }, []);

  const fetchCVByUserId = async (userId) => {
    try {
      const response = await fetch(`${API_BASE}/cvs/${userId}`);
      if (!response.ok) {
        throw new Error("Feil ved henting av CV");
      }
      const data = await response.json();
      return data;
    } catch (error) {
      console.error("Feil ved henting av CV:", error);
      throw error;
    }
  };

  // Registrer ny CV
  const addCV = (e) => {
    e.preventDefault();
    const cvToCreate = { ...newCV, userId: "innlogget_bruker_id" }; // Erstatt med faktisk innlogget bruker-ID
    createCV(cvToCreate)
      .then((data) => {
        setCvs([...cvs, data]);
        setNewCV({
          personalInfo: { name: "", email: "", phone: "" },
          skills: [],
          education: [],
          experience: [],
          references: [],
        });
      })
      .catch((error) => console.error("Feil ved oppretting av CV:", error));
  };

  // Rediger CV
  const handleEditCV = (cv) => {
    setEditingCV(cv);
    setNewCV(cv);
  };

  const handleUpdateCV = (e) => {
    e.preventDefault();
    if (userId && editingCV && editingCV.userId === userId) {
      updateCV(editingCV._id, newCV)
        .then((data) => {
          setCvs([data]); // Oppdater den eksisterende CV-en
          setEditingCV(null);
          setNewCV({
            personalInfo: { name: "", email: "", phone: "" },
            skills: [],
            education: [],
            experience: [],
            references: [],
          });
        })
        .catch((error) => console.error("Feil ved oppdatering av CV:", error));
    } else {
      console.error("Du kan bare oppdatere din egen CV");
    }
  };

  // Slett CV
  const removeCV = (id) => {
    if (userId && id === editingCV._id) {
      deleteCV(id)
        .then(() => setCvs([])) // Fjern CV-en etter sletting
        .catch((error) => console.error("Feil ved sletting av CV:", error));
    } else {
      console.error("Du kan bare slette din egen CV");
    }
  };

  // Filtrer CV-er basert på søk
  const filteredCVs = cvs.filter(
    (cv) =>
      cv.personalInfo.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      cv.personalInfo.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="p-8 bg-gradient-to-r from-[#90E0EF] to-[#CAF0F8] min-h-screen">
      <Navbar />

      <h1 className="mb-20 flex items-center justify-center text-3xl font-bold text-[#023E8A]">
        CV-administrasjon
      </h1>

      <form
        onSubmit={editingCV ? handleUpdateCV : addCV}
        className="max-w-sm p-10 mb-8 bg-white rounded-lg shadow-lg"
      >
        <h2 className="text-center mb-4 text-xl text-[#023E8A]">
          {editingCV ? "Rediger CV" : "Registrer ny CV"}
        </h2>

        <InputField
          type="text"
          placeholder="Navn"
          value={newCV.personalInfo.name}
          onChange={(e) =>
            setNewCV({
              ...newCV,
              personalInfo: { ...newCV.personalInfo, name: e.target.value },
            })
          }
          required
        />

        <InputField
          type="email"
          placeholder="E-post"
          value={newCV.personalInfo.email}
          onChange={(e) =>
            setNewCV({
              ...newCV,
              personalInfo: { ...newCV.personalInfo, email: e.target.value },
            })
          }
          required
        />

        <InputField
          type="text"
          placeholder="Telefon"
          value={newCV.personalInfo.phone}
          onChange={(e) =>
            setNewCV({
              ...newCV,
              personalInfo: { ...newCV.personalInfo, phone: e.target.value },
            })
          }
          required
        />

        {/* Legg til flere inputfelter for ferdigheter, utdanning, erfaring, referanser */}

        <div className="flex justify-center w-full">
          <button
            type="submit"
            className="mt-16 p-3 text-white bg-[#0077B6] rounded-lg hover:bg-[#023E8A] focus:ring-4 focus:ring-[#90E0EF]"
          >
            {editingCV ? "Oppdater CV" : "Registrer CV"}
          </button>
        </div>
      </form>

      <h2 className="mb-4 text-xl text-[#023E8A]">
        SØK ETTER EKSISTERENDE CV-er:
      </h2>
      <div className="mb-8">
        <input
          type="text"
          placeholder="Søk etter CV..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="p-3 w-full border-2 border-[#023E8A] rounded-lg focus:outline-none focus:ring-2 focus:ring-[#0077B6]"
        />
      </div>

      <ul className="space-y-10 max-w-72">
        {filteredCVs.map((cv) => (
          <li
            key={cv._id}
            className="p-6 transition-transform transform bg-white border border-gray-200 rounded-lg shadow-md hover:scale-105"
          >
            <div className="space-y-2">
              <p className="text-lg font-medium text-gray-700">
                <strong className="text-[#023E8A]">Navn:</strong>{" "}
                {cv.personalInfo.name}
              </p>
              <p className="text-lg font-medium text-gray-700">
                <strong className="text-[#023E8A]">E-post:</strong>{" "}
                {cv.personalInfo.email}
              </p>
              <p className="text-lg font-medium text-gray-700">
                <strong className="text-[#023E8A]">Telefon:</strong>{" "}
                {cv.personalInfo.phone}
              </p>
            </div>

            <button
              onClick={() => handleEditCV(cv)}
              className="mt-4 px-4 py-2 text-white bg-yellow-500 rounded-lg hover:bg-yellow-600 focus:outline-none focus:ring-4 focus:ring-[#CAF0F8]"
              aria-label={`Rediger CV ${cv.personalInfo.name}`}
            >
              Rediger
            </button>

            <button
              onClick={() => removeCV(cv._id)}
              className="mt-4 px-4 py-2 text-white bg-red-500 rounded-lg hover:bg-red-600 focus:outline-none focus:ring-4 focus:ring-[#CAF0F8] focus:ring-opacity-50 transition-colors"
              aria-label={`Slett CV ${cv.personalInfo.name}`}
            >
              Slett
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default UserCV;
