import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
} from "react-router-dom";
import Login from "./components/Login";
import Users from "./components/Users";
import CV from "./components/CV"; // CV-komponenten
import PDFExport from "./components/PDFExport";

function App() {
  // Hent token og rolle fra localStorage
  const token = localStorage.getItem("token");
  const role = localStorage.getItem("role"); // For eksempel, lagre "admin" eller "user" ved login

  // Beskyttede ruter for admin
  const ProtectedRoute = ({ children }) => {
    if (!token) {
      return <Navigate to="/" />; // Hvis ikke logget inn, send til login
    }
    return children;
  };

  // Beskyttede ruter for admin
  const AdminRoute = ({ children }) => {
    if (!token || role !== "admin") {
      return <Navigate to="/" />; // Hvis ikke admin, send til login
    }
    return children;
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login />} />

        {/* Beskyttede ruter for admin */}
        <Route element={<AdminRoute />}>
          <Route path="/users" element={<Users />} />
          <Route path="/export" element={<PDFExport />} />
        </Route>

        {/* Rute for CV som skal v√¶re tilgjengelig for alle som er logget inn */}
        <Route element={<ProtectedRoute />}>
          <Route path="/cvs" element={<CV />} />
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
