import React, { useRef, useState } from "react";
import CVForm from "../components/CVForm";
import { createCV } from "../services/api";
import Navbar from "../components/Navbar";
import { jsPDF } from "jspdf";

function UserCV() {
  const formRef = useRef(); // Referanse til skjemaet
  const [cvData, setCvData] = useState(null);

  const handleCVSubmit = (data) => {
    // Sender data til API'et for å opprette en CV
    createCV(data)
      .then((response) => {
        console.log("CV opprettet:", response);
        setCvData(data); // Lagre CV-data
      })
      .catch((error) => {
        console.error("Feil ved oppretting av CV:", error);
        alert("Kunne ikke opprette CV");
      });
  };

  const exportToPDF = () => {
    if (!cvData) {
      alert("Ingen CV-data å eksportere");
      return;
    }

    const doc = new jsPDF();
    doc.setFontSize(20);
    doc.text("CV - " + cvData.personalInfo.name, 10, 10);

    // Personlig informasjon
    doc.setFontSize(14);
    doc.text("Navn: " + cvData.personalInfo.name, 10, 30);
    doc.text("E-post: " + cvData.personalInfo.email, 10, 40);
    doc.text("Telefon: " + cvData.personalInfo.phone, 10, 50);

    // Ferdigheter
    doc.text("Ferdigheter:", 10, 70);
    doc.text(cvData.skills || "Ingen ferdigheter oppgitt", 10, 80);

    // Utdanning
    doc.text("Utdanning:", 10, 100);
    doc.text(cvData.education || "Ingen utdanning oppgitt", 10, 110);

    // Erfaring
    doc.text("Erfaring:", 10, 130);
    doc.text(cvData.experience || "Ingen erfaring oppgitt", 10, 140);

    // Referanser
    doc.text("Referanser:", 10, 160);
    doc.text(cvData.references || "Ingen referanser oppgitt", 10, 170);

    doc.save(cvData.personalInfo.name + "_CV.pdf");
  };

  return (
    <div className="p-6">
      <Navbar />
      <div>
        <h1 className="mb-6 text-xl">Opprett CV</h1>
        <form
          ref={formRef}
          onSubmit={(e) => {
            e.preventDefault();
            handleCVSubmit();
          }}
        >
          <CVForm onSubmit={handleCVSubmit} />

          <button
            onClick={exportToPDF}
            type="button"
            className="p-3 mt-4 text-white bg-blue-500 rounded-lg"
          >
            Eksporter til PDF
          </button>
        </form>
      </div>
    </div>
  );
}

export default UserCV;
