return (
  <div className="flex items-center justify-center h-screen bg-gradient-to-r from-blue-400 to-indigo-600">
    <form
      className="max-w-sm p-10 bg-white rounded-lg shadow-lg w-96"
      onSubmit={handleSubmit(onSubmit)}
    >
      <h2 className="mb-6 text-3xl font-bold text-center text-gray-800">
        Velkommen tilbake!
      </h2>

      <label className="block mb-2 text-sm font-semibold text-gray-700">
        Brukernavn
      </label>
      <input
        type="text"
        {...register("username", { required: true })}
        placeholder="Skriv inn brukernavn"
        className="w-full p-3 mb-4 border-2 border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
      />

      <label className="block mb-2 text-sm font-semibold text-gray-700">
        Passord
      </label>
      <input
        type="password"
        {...register("password", { required: true })}
        className="w-full p-3 mb-6 border-2 border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
      />

      <button
        type="submit"
        className="w-full p-3 text-white bg-indigo-600 rounded-lg hover:bg-indigo-700 focus:ring-4 focus:ring-indigo-300"
      >
        Logg inn
      </button>

