import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { createCV, fetchCVsByUserId } from "../services/api"; // Importer API-funksjoner
import CVForm from "../components/CVForm"; // Skjema for å lage CV
import Navbar from "../components/Navbar";

function UserCV() {
  const navigate = useNavigate();
  const [cv, setCv] = useState(null); // State for CV
  const [loading, setLoading] = useState(true); // Loading state

  // Hent brukerens CV ved initialisering
  useEffect(() => {
    const currentUserId = localStorage.getItem("userId");
    if (!currentUserId) {
      alert("Brukeren er ikke logget inn.");
      navigate("/login"); // Naviger til login-siden
      return;
    }

    fetchCVsByUserId(currentUserId) // Hent CV-en for den innloggede brukeren
      .then((response) => {
        if (response.length > 0) {
          setCv(response[0]); // Sett første CV i state (det er bare én CV per bruker)
        }
        setLoading(false); // Sett loading til false
      })
      .catch((error) => {
        console.error("Feil ved henting av CV:", error);
        setLoading(false); // Sett loading til false uansett feil
      });
  }, [navigate]);

  // Håndter CV-oppdatering
  const handleCVSubmit = (data) => {
    const currentUserId = localStorage.getItem("userId");

    if (!currentUserId) {
      alert("Brukeren er ikke logget inn.");
      navigate("/login"); // Naviger til login-siden
      return;
    }

    const newCv = {
      ...data,
      userId: currentUserId,
    };

    createCV(newCv)
      .then((response) => {
        console.log("CV opprettet:", response);
        // Etter opprettelse, hent CV-en på nytt
        fetchCVsByUserId(currentUserId)
          .then((newCvResponse) => {
            setCv(newCvResponse[0]); // Sett den opprettede CV-en i state
          })
          .catch((error) => {
            console.error("Feil ved henting av CV etter opprettelse:", error);
          });
      })
      .catch((error) => {
        console.error("Feil ved oppretting av CV:", error);
        alert("Kunne ikke opprette CV");
      });
  };

  if (loading) {
    return <div>Laster CV...</div>; // Lastingsindikator
  }

  return (
    <div className="p-6">
      <Navbar />
      <h1 className="mb-4 text-3xl font-semibold">Min CV</h1>

      {cv ? (
        <div className="p-6 bg-white rounded-lg shadow-lg">
          <h2 className="mb-4 text-2xl font-semibold">
            {cv.personalInfo.name}
          </h2>
          <p>
            <strong>E-post:</strong> {cv.personalInfo.email}
          </p>
          <p>
            <strong>Telefon:</strong> {cv.personalInfo.phone}
          </p>

          <h3 className="mt-6 text-xl font-semibold">Utdanning</h3>
          {cv.education.map((edu, index) => (
            <div key={index} className="mb-2">
              <p>
                <strong>Institusjon:</strong> {edu.institution}
              </p>
              <p>
                <strong>Grad:</strong> {edu.degree}
              </p>
              <p>
                <strong>År:</strong> {edu.year}
              </p>
            </div>
          ))}

          <h3 className="mt-6 text-xl font-semibold">Erfaring</h3>
          {cv.experience.length > 0 ? (
            cv.experience.map((exp, index) => (
              <div key={index} className="mb-2">
                <p>
                  <strong>Firma:</strong> {exp.company}
                </p>
                <p>
                  <strong>Rolle:</strong> {exp.role}
                </p>
                <p>
                  <strong>Periode:</strong> {exp.period}
                </p>
              </div>
            ))
          ) : (
            <p>Ingen erfaring oppgitt</p>
          )}

          <h3 className="mt-6 text-xl font-semibold">Ferdigheter</h3>
          {cv.skills.length > 0 ? (
            <ul>
              {cv.skills.map((skill, index) => (
                <li key={index}>{skill}</li>
              ))}
            </ul>
          ) : (
            <p>Ingen ferdigheter oppgitt</p>
          )}

          <h3 className="mt-6 text-xl font-semibold">Referanser</h3>
          {cv.references.length > 0 ? (
            cv.references.map((ref, index) => (
              <div key={index} className="mb-2">
                <p>
                  <strong>Navn:</strong> {ref.name}
                </p>
                <p>
                  <strong>Kontaktinfo:</strong> {ref.contactInfo}
                </p>
              </div>
            ))
          ) : (
            <p>Ingen referanser oppgitt</p>
          )}
        </div>
      ) : (
        <div>Du har ikke opprettet en CV ennå.</div>
      )}

      <CVForm onSubmit={handleCVSubmit} />
    </div>
  );
}

export default UserCV;
