const API_BASE = process.env.REACT_APP_API_BASE;

//Hent alle brukere
export const fetchUsers = async () => {
  try {
    const response = await fetch(`${API_BASE}/users`);
    if (!response.ok) {
      throw new Error("Feil ved henting av brukere");
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error(error);
    throw error;
  }
};

//Registrer ny bruker
export const createUser = async (newUser) => {
  try {
    const response = await fetch(`${API_BASE}/users`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newUser),
    });
    if (!response.ok) {
      throw new Error("Feil ved oppretting av bruker");
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error(error);
    throw error;
  }
};

// Oppdater bruker
export const updateUser = async (id, updatedUser) => {
  try {
    const response = await fetch(`${API_BASE}/users/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updatedUser),
    });

    if (!response.ok) {
      throw new Error("Feil ved oppdatering av bruker");
    }

    // Kun les JSON hvis responsen ikke er tom
    let data = null;
    if (response.status !== 204) {
      data = await response.json(); // Kun kall json() én gang
    }

    return data;
  } catch (error) {
    console.error(error);
    throw error;
  }
};

//Slett bruker
export const deleteUser = async (id) => {
  try {
    const response = await fetch(`${API_BASE}/users/${id}`, {
      method: "DELETE",
    });
    if (!response.ok) {
      throw new Error("Feil ved sletting av bruker");
    }
  } catch (error) {
    console.error(error);
    throw error;
  }
};

//Hent alle CV
export const fetchAllCVs = async () => {
  try {
    const response = await fetch(`${API_BASE}/cvs`);
    if (!response.ok) {
      throw new Error("Feil ved henting av CV´er");
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error(error);
    throw error;
  }
};
