import React from "react";
import { Navigate, Outlet } from "react-router-dom";

const adminRoutes = () => {
  const token = localStorage.getItem("token"); // Hent tokenet fra lokal lagring

  if (!token) {
    // Hvis det ikke finnes et token, send brukeren til innloggingssiden
    return <Navigate to="/" />;
  }

  // Hvis token finnes, render innholdet til beskyttede ruter
  return <Outlet />;
};

export default PrivRoute;
