import React from "react";
import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";

const API_BASE = process.env.REACT_APP_API_BASE;

function LoginPage() {
  const { register, handleSubmit } = useForm();
  const navigate = useNavigate();

  const onSubmit = (data) => {
    const { username, password } = data;

    // Hent alle brukere fra CRUD CRUD API
    fetch(`${API_BASE}/users`)
      .then((response) => response.json())
      .then((users) => {
        // Finn en bruker som matcher både brukernavn og passord
        const user = users.find(
          (user) => user.username === username && user.password === password
        );

        if (user) {
          // Lagre brukerens rolle og brukernavn i localStorage 
          localStorage.setItem("userId", user._id);
          localStorage.setItem("role", user.role);
          localStorage.setItem("name", user.name); // Lagre brukernavnet hvis nødvendig
          navigate("/dashboard"); // Naviger til dashboardet
        } else {
          alert("Feil brukernavn eller passord.");
        }
      })
      .catch((error) => {
        console.error("Feil ved henting av brukere:", error);
        alert(
          "Det oppstod en feil ved serverkommunikasjon. Vennligst prøv igjen senere."
        );
      });
  };

