import React, { useState, useEffect } from "react";
import { getCVs, createCV } from "../api"; // Importer nødvendige API-funksjoner
import { useNavigate } from "react-router-dom";

function CV() {
  const [cvs, setCvs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [name, setName] = useState("");
  const [skills, setSkills] = useState("");
  const [experience, setExperience] = useState("");
  const [education, setEducation] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Du må være innlogget for å vise CV-er");
      return;
    }

    const fetchCVs = async () => {
      try {
        const fetchedCVs = await getCVs(token);
        setCvs(fetchedCVs);
        setLoading(false);
      } catch (error) {
        console.error("Feil ved henting av CV-er:", error);
        setLoading(false);
      }
    };

    fetchCVs();
  }, []);

  const handleCreateCV = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Du må være innlogget for å opprette en CV");
      return;
    }

    const cvData = {
      name,
      skills,
      experience,
      education,
    };

    try {
      await createCV(cvData, token); // Opprett CV med token
      alert("CV opprettet!");
      navigate("/cvs"); // Naviger tilbake til CV-siden for å vise oppdatert liste
    } catch (error) {
      console.error("Feil ved opprettelse av CV:", error);
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h2 className="mb-4 text-center">Mine CV-er</h2>

      {cvs.length === 0 ? (
        <p>
          Du har ingen CV-er. <a href="/cvs">Opprett en ny CV</a>
        </p>
      ) : (
        <ul>
          {cvs.map((cv) => (
            <li key={cv._id}>
              <h3>{cv.name}</h3>
              <p>Ferdigheter: {cv.skills}</p>
              <p>Erfaring: {cv.experience}</p>
              <p>Utdanning: {cv.education}</p>
            </li>
          ))}
        </ul>
      )}

      {/* Skjema for å opprette en ny CV */}
      <form
        onSubmit={handleCreateCV}
        className="p-8 mt-8 bg-white rounded shadow-md"
      >
        <h2 className="mb-6 text-2xl font-bold text-center">
          Opprett en ny CV
        </h2>

        <label className="block mb-2 text-gray-700">Navn:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="w-full p-2 mb-4 border rounded"
          required
        />

        <label className="block mb-2 text-gray-700">Ferdigheter:</label>
        <input
          type="text"
          value={skills}
          onChange={(e) => setSkills(e.target.value)}
          className="w-full p-2 mb-4 border rounded"
          required
        />

        <label className="block mb-2 text-gray-700">Erfaring:</label>
        <input
          type="text"
          value={experience}
          onChange={(e) => setExperience(e.target.value)}
          className="w-full p-2 mb-4 border rounded"
          required
        />

        <label className="block mb-2 text-gray-700">Utdanning:</label>
        <input
          type="text"
          value={education}
          onChange={(e) => setEducation(e.target.value)}
          className="w-full p-2 mb-4 border rounded"
          required
        />

        <button
          type="submit"
          className="w-full p-2 text-white bg-blue-500 rounded hover:bg-blue-600"
        >
          Lagre CV
        </button>
      </form>
    </div>
  );
}

export default CV;
