import React from "react";
import { useForm, useFieldArray } from "react-hook-form";

function CVForm({ onSubmit }) {
  const { register, handleSubmit, control } = useForm();

  const {
    fields: skills,
    append: addSkill,
    remove: removeSkill,
  } = useFieldArray({ control, name: "skills" });

  const {
    fields: education,
    append: addEducation,
    remove: removeEducation,
  } = useFieldArray({ control, name: "education" });

  const {
    fields: experience,
    append: addExperience,
    remove: removeExperience,
  } = useFieldArray({ control, name: "experience" });

  const {
    fields: references,
    append: addReference,
    remove: removeReference,
  } = useFieldArray({ control, name: "references" });

  const onFormSubmit = (data) => {
    const formattedData = {
      personalInfo: {
        name: data.name,
        email: data.email,
        phone: data.phone,
      },
      skills: data.skills.map((s) => s.name),
      education: data.education.map((e) => ({
        institution: e.institution,
        degree: e.degree,
        year: e.year,
      })),
      experience: data.experience.map((e) => ({
        title: e.jobTitle,
        company: e.company,
        years: `${e.startDate} - ${e.endDate}`,
      })),
      references: data.references.map((r) => ({
        name: r.name,
        contactInfo: r.contactInfo,
      })),
    };
    onSubmit(formattedData);
  };

  return (
    <form
      onSubmit={handleSubmit(onFormSubmit)}
      className="p-6 bg-white rounded-lg shadow-md"
    >
      <h2 className="mb-4 text-2xl font-semibold">Personlig informasjon</h2>
      <label>Navn</label>
      <input
        type="text"
        {...register("name", { required: true })}
        className="w-full p-3 mb-4 border rounded-md"
      />

      <label>E-post</label>
      <input
        type="email"
        {...register("email", { required: true })}
        className="w-full p-3 mb-4 border rounded-md"
      />

      <label>Telefonnummer</label>
      <input
        type="num"
        {...register("phone", { required: true })}
        className="w-full p-3 mb-4 border rounded-md"
      />

      <h2 className="mt-6 mb-4 text-2xl font-semibold">Ferdigheter</h2>
      {skills.map((item, index) => (
        <div key={item.id} className="flex items-center mb-4">
          <input
            type="text"
            {...register(`skills.${index}.name`, { required: true })}
            className="w-full p-3 border rounded-md"
            placeholder="Ferdighet"
          />
          <button
            type="button"
            className="p-2 ml-2 text-white bg-red-500 rounded-md"
            onClick={() => removeSkill(index)}
          >
            Fjern
          </button>
        </div>
      ))}
      <button
        type="button"
        className="w-full p-3 mb-6 text-white bg-green-500 rounded-md"
        onClick={() => addSkill({ name: "" })}
      >
        Legg til ferdighet
      </button>

      <h2 className="mt-6 mb-4 text-2xl font-semibold">Utdanning</h2>
      {education.map((item, index) => (
        <div key={item.id} className="mb-4">
          <label>Institusjon</label>
          <input
            type="text"
            {...register(`education.${index}.institution`, { required: true })}
            className="w-full p-3 mb-2 border rounded-md"
          />
          <label>Grad</label>
          <input
            type="text"
            {...register(`education.${index}.degree`, { required: true })}
            className="w-full p-3 mb-2 border rounded-md"
          />
          <label>Ã…r</label>
          <input
            type="text"
            {...register(`education.${index}.year`, { required: true })}
            className="w-full p-3 mb-2 border rounded-md"
          />
          <button
            type="button"
            className="p-2 mt-2 text-white bg-red-500 rounded-md"
            onClick={() => removeEducation(index)}
          >
            Fjern utdanning
          </button>
        </div>
      ))}
      <button
        type="button"
        className="w-full p-3 mb-6 text-white bg-green-500 rounded-md"
        onClick={() => addEducation({ institution: "", degree: "", year: "" })}
      >
        Legg til utdanning
      </button>

      <h2 className="mt-6 mb-4 text-2xl font-semibold">Referanser</h2>
      {references.map((item, index) => (
        <div key={item.id} className="mb-4">
          <label>Navn</label>
          <input
            type="text"
            {...register(`references.${index}.name`, { required: true })}
            className="w-full p-3 mb-2 border rounded-md"
          />
          <label>Kontaktinformasjon</label>
          <input
            type="text"
            {...register(`references.${index}.contactInfo`, { required: true })}
            className="w-full p-3 mb-2 border rounded-md"
          />
          <button
            type="button"
            className="p-2 mt-2 text-white bg-red-500 rounded-md"
            onClick={() => removeReference(index)}
          >
            Fjern referanse
          </button>
        </div>
      ))}
      <button
        type="button"
        className="w-full p-3 mb-6 text-white bg-green-500 rounded-md"
        onClick={() => addReference({ name: "", contactInfo: "" })}
      >
        Legg til referanse
      </button>

      <button
        type="submit"
        className="w-full p-3 text-white bg-blue-500 rounded-md hover:bg-blue-600"
      >
        Lagre CV
      </button>
    </form>
  );
}

export default CVForm;
