import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { createCV, updateCV, fetchAllCVs, deleteCV } from "../services/api"; // Importer nødvendige API-funksjoner
import InputField from "../components/InputField"; // Sørg for at du har en InputField-komponent

const UserCV = () => {
    const [cv, setCv] = useState({
      personalInfo: { name: "", email: "", phone: "" },
      skills: [],
      education: [{ institution: "", degree: "", year: "" }],
      experience: [{ title: "", company: "", years: "" }],
    });
    const [loading, setLoading] = useState(false);
    const [isEditing, setIsEditing] = useState(false);
    const { id } = useParams(); 
    const navigate = useNavigate();

    useEffect(() => {
      if (id) {
        setIsEditing(true);
        fetchCV();
      }
    }, [id]);

    const fetchCV = async () => {
      setLoading(true);
      try {
        const response = await fetchAllCVs();
        const userCV = response.find((cv) => cv._id === id);
        if (userCV) {
          setCv(userCV);
        }
      } catch (error) {
        console.error("Feil ved henting av CV:", error);
      } finally {
        setLoading(false);
      }
    };

    const handleChange = (e) => {
      const { name, value } = e.target;
      const [mainKey, subKey] = name.split('.'); // For å håndtere nested felter som "personalInfo.name"

      setCv((prevCv) => ({
        ...prevCv,
        [mainKey]: {
          ...prevCv[mainKey],
          [subKey]: value,
        },
      }));
    };

    const handleSkillChange = (e) => {
      setCv({
        ...cv,
        skills: e.target.value.split(", "),
      });
    };

    const handleEducationChange = (e, index) => {
      const { name, value } = e.target;
      const updatedEducation = [...cv.education];
      updatedEducation[index][name] = value;
      setCv({ ...cv, education: updatedEducation });
    };

    const handleExperienceChange = (e, index) => {
      const { name, value } = e.target;
      const updatedExperience = [...cv.experience];
      updatedExperience[index][name] = value;
      setCv({ ...cv, experience: updatedExperience });
    };

    const handleAddEducation = () => {
      setCv({
        ...cv,
        education: [...cv.education, { institution: "", degree: "", year: "" }],
      });
    };

    const handleAddExperience = () => {
      setCv({
        ...cv,
        experience: [...cv.experience, { title: "", company: "", years: "" }],
      });
    };

    const handleSubmit = async (e) => {
      e.preventDefault();
      setLoading(true);
      try {
        if (isEditing) {
          await updateCV(id, cv);
        } else {
          await createCV(cv);
        }
        navigate("/dashboard"); // Naviger tilbake til dashboard etter oppretting/oppdatering
      } catch (error) {
        console.error("Feil ved lagring av CV:", error);
      } finally {
        setLoading(false);
      }
    };

    const handleDelete = async () => {
      setLoading(true);
      try {
        await deleteCV(id);
        navigate("/dashboard"); // Naviger tilbake til dashboard etter sletting
      } catch (error) {
        console.error("Feil ved sletting av CV:", error);
      } finally {
        setLoading(false);
      }
    };

    return (
      <div className="p-8 bg-gradient-to-r from-[#90E0EF] to-[#CAF0F8] min-h-screen">
        <h1 className="mb-20 flex items-center justify-center text-3xl font-bold text-[#023E8A]">
          {isEditing ? "Rediger CV" : "Opprett CV"}
        </h1>

        {loading && <p>Lasting...</p>}

        <form
          onSubmit={handleSubmit}
          className="max-w-lg p-10 mb-8 bg-white rounded-lg shadow-lg "
        >
          <h2 className="text-center mb-4 text-xl text-[#023E8A]">
            {isEditing ? "Rediger CV" : "Opprett CV"}
          </h2>

          <InputField
            type="text"
            placeholder="Navn"
            name="personalInfo.name"
            value={cv.personalInfo.name}
            onChange={handleChange}
            required
          />

          <InputField
            type="email"
            placeholder="E-post"
            name="personalInfo.email"
            value={cv.personalInfo.email}
            onChange={handleChange}
            required
          />

          <InputField
            type="text"
            placeholder="Telefonnummer"
            name="personalInfo.phone"
            value={cv.personalInfo.phone}
            onChange={handleChange}
            required
          />

          <InputField
            type="text"
            placeholder="Ferdigheter (separert med komma)"
            value={cv.skills.join(", ")}
            onChange={handleSkillChange}
          />

          <h3 className="mb-2 text-[#023E8A]">Utdanning:</h3>
          {cv.education.map((edu, index) => (
            <div key={index} className="mb-4">
              <InputField
                type="text"
                placeholder="Institusjon"
                name="institution"
                value={edu.institution}
                onChange={(e) => handleEducationChange(e, index)}
              />
              <InputField
                type="text"
                placeholder="Grad"
                name="degree"
                value={edu.degree}
                onChange={(e) => handleEducationChange(e, index)}
              />
              <InputField
                type="text"
                placeholder="År"
                name="year"
                value={edu.year}
                onChange={(e) => handleEducationChange(e, index)}
              />
            </div>
          ))}
          <button
            type="button"
            onClick={handleAddEducation}
            className="text-[#0077B6] mb-4"
          >
            Legg til utdanning
          </button>

          <h3 className="mb-2 text-[#023E8A]">Erfaring:</h3>
          {cv.experience.map((exp, index) => (
            <div key={index} className="mb-4">
              <InputField
                type="text"
                placeholder="Tittel"
                name="title"
                value={exp.title}
                onChange={(e) => handleExperienceChange(e, index)}
              />
              <InputField
                type="text"
                placeholder="Selskap"
                name="company"
                value={exp.company}
                onChange={(e) => handleExperienceChange(e, index)}
              />
              <InputField
                type="text"
                placeholder="År"
                name="years"
                value={exp.years}
                onChange={(e) => handleExperienceChange(e, index)}
              />
            </div>
          ))}
          <button
            type="button"
            onClick={handleAddExperience}
            className="text-[#0077B6] mb-4"
          >
            Legg til erfaring
          </button>

          <div className="flex justify-center w-full">
            <button
              type="submit"
              className="mt-8 p-3 text-white bg-[#0077B6] rounded-lg hover:bg-[#023E8A] focus:ring-4 focus:ring-[#90E0EF]"
            >
              {isEditing ? "Oppdater CV" : "Opprett CV"}
            </button>
          </div>
        </form>

        {isEditing && (
          <button
            onClick={handleDelete}
            className="mt-4 px-4 py-2 text-white bg-red-500 rounded-lg hover:bg-red-600 focus:outline-none focus:ring-4 focus:ring-[#CAF0F8]"
          >
            Slett CV
          </button>
        )}
      </div>
    );
  };

  export default UserCV;
